mapper.select:
SELECT SEQ, TITLE, CONTENT, CONDITION, DT, C_FLAG 
FROM MAISON_CONTENT
WHERE SEQ=#{seq}

SELECT * 
FROM MAISON_USER
WHERE ID = #{id} AND PWD = #{pwd}

mapper.update:
UPDATE MAISON_USER 
SET PWD=#{pwd}, NAME=#{name}, PHONE=#{phone}, ADDRESS=#{address}, EMAIL=#{email} 
WHERE SEQ=#{seq}

mapper.select:

SELECT ROWNUM, P.SEQ, P.TITLE, P.CONTENT, P.DT, P.FILE_PATH
FROM(
      (SELECT ROWNUM AS RN, K.*  
      FROM(
      	SELECT * FROM MAISON_CONTENT 
            WHERE C_FLAG = 'U'
          ORDER BY SEQ DESC)K
      WHERE ROWNUM <= #{end_content}
      )P
) WHERE RN >= #{start_content}
